(function () {
    function f() {
        var a = document.querySelectorAll(".accordion");
        a.forEach(function (b) {
            b.querySelector(".accordion__head").addEventListener(
                "click",
                function () {
                    g(a, b);
                },
                !1
            );
        });
    }
    function g(a, b) {
        var c = b.selected;
        a.forEach(function (d) {
            d.selected = !1;
        });
        b.selected = !c;
        a.forEach(function (d) {
            var e = d.querySelector(".accordion__container");
            d.selected ? ((e.style.height = d.querySelector(".accordion__body").offsetHeight + "px"), d.setAttribute("aria-expanded", !0)) : ((e.style.height = 0), d.setAttribute("aria-expanded", !1));
        });
    }
    function h() {
        var a = document.querySelector(".nav__slide"),
            b = document.querySelector(".main"),
            c = document.querySelector(".nav__header-toggler"),
            d = document.querySelectorAll(".js-links");
        c.addEventListener("click", function () {
            a.classList.toggle("nav__slide--open");
            c.classList.toggle("nav_hamburger");
            b.classList.toggle("nav_open");
        });
        d.forEach(function (e) {
            return e.addEventListener("click", function () {
                a.classList.remove("nav__slide--open");
                c.classList.contains("nav_hamburger") && b.classList.toggle("nav_open");
                c.classList.remove("nav_hamburger");
            });
        });
    }
    new (function () {
        f();
    })();
    new (function () {
        h();
    })();
}.call(window));
